mapper.select:
SELECT *
FROM appointment

	AND id=#{id}
	AND student=#{studentId}
	AND teacher=#{teacherId}
	AND state=#{state}
	AND isDelete=#{isDelete}
	AND isSend=#{isSend}

mapper.update:
UPDATE appointment
student_sign_in_time=#{studentSignInTime}, startTime=#{startTime}, endTime=#{endTime}, state=#{state}, isDelete=#{isDelete}, isSend=#{isSend},
WHERE id=#{id}

UPDATE appointment 

	student_sign_in_time=#{appointment.studentSignInTime},
	startTime=#{appointment.startTime},
	endTime=#{appointment.endTime},
	state=#{appointment.state},
	isDelete=#{appointment.isDelete},
	isSend=#{appointment.isSend},


	AND id=#{condition.id}
	AND student=#{condition.studentId}
	AND teacher=#{condition.teacherId}
	AND state=#{condition.state}
	AND isDelete=#{condition.isDelete}
	AND isSend=#{condition.isSend}
